import { useSEO } from '@upendra.manike/seo-boost';

function HomePage() {
  useSEO({
    title: 'Home | My App',
    description: 'Best platform',
    openGraph: { image: 'https://example.com/og.jpg' },
  });
  return <h1>Welcome</h1>;
}

import { useSEO, createArticleSchema } from '@upendra.manike/seo-boost';

function ArticlePage() {
  useSEO({
    title: 'Article Title',
    jsonLd: createArticleSchema({
      headline: 'Article Title',
      description: 'Article description',
      datePublished: '2024-01-01',
      author: { '@type': 'Person', name: 'Author' },
      publisher: { '@type': 'Organization', name: 'Publisher' },
    }),
  });
  return <article>Content</article>;
}

import { generateSitemap } from '@upendra.manike/seo-boost';

const sitemap = generateSitemap({
  urls: [
    { loc: 'https://example.com/', changefreq: 'daily', priority: 1.0 },
    { loc: 'https://example.com/about', changefreq: 'monthly', priority: 0.8 },
  ],
});

// Save to file or serve as API route

import { analyzeSEO } from '@upendra.manike/seo-boost';

const analysis = analyzeSEO();
console.log(`SEO Score: ${analysis.score}/100`);
console.log('Issues:', analysis.issues);
console.log('Recommendations:', analysis.recommendations);
